/**
 * Estrutura de informações da divisão de um segredo M de N.
 * @property {number} secret Segredo que foi dividido em várias partes.
 * @property {number} entropy Dados de entropia retornados pelo HSM. Não é necessário para a operação de M de N. Pode ser utilizado, por exemplo, como seed em operações KDF.
 * @property {number} parts Partes geradas no M de N.
 */
export default interface MofNSplit {
    secret: Buffer;
    entropy: Buffer;
    parts: Buffer[];
}
