import AuthUsernamePassword from "./auth_username_password";
/**
 * Opções disponíveis para o cliente do HSM
 * @interface HsmOptions
 * @property {string} host - Endereço do HSM.
 * @property {number} port - Porta do HSM.
 * @property {boolean} enableTrace - Habilita as mensagens do OpenSSL.
 * @property {AuthUsernamePassword} authUsernamePassword - Autenticação com usuário e senha (OBS: Todas as propriedades são obrigatórias se especificado).
 * @property {string} authToken - Autenticação com token.
 */
export default interface HsmOptions {
    host: string;
    port?: number;
    enableTrace?: boolean;
    authUsernamePassword?: AuthUsernamePassword;
    authToken?: string;
}
