/**
 * Opções de configuração TLS disponíveis para o cliente do HSM
 * @interface TlsOptions
 * @property {string | Buffer | (string | Buffer)[]} key - Chave privada do client (obrigatório para mTLS).
 * @property {string | Buffer | (string | Buffer)[]} cert - Certificado em formato PEM (obrigatório para mTLS).
 * @property {string | Buffer | (string | Buffer)[]} ca - Cadeia de certificados confiáveis.
 */
export default interface TlsOptions {
    key?: string | Buffer | (string | Buffer)[];
    cert?: string | Buffer | (string | Buffer)[];
    ca?: string | Buffer | (string | Buffer)[];
}
